 *      **EXPLICAÇÃO EM Card.jsx
 * 
 */

// FORMA 2
export default _ =>
    <div id="app">
        <h1>Fundamentos React 22</h1>
        <Card titulo="Desafio Aleatorio">
            <Aleatorio min={1} max={60}/>
            <Aleatorio min={1} max={60}/>
        </Card>
        <Fragmento />
        <ComParametro  
            titulo="Situação do Aluno"
            nome="Angelina Pierre"
            nota={9.3}
        />
        <ComParametro
            titulo="Situação do aluno"
            nome="Santa Kaya"
            nota="0.4"
        />
        <Primeiro> </Primeiro>
    </div>


