        <h1>Fundamentos React 24</h1>
        <div className="Cards">
            <Card 
                titulo="#04 - Desafio Aleatorio" 
                color="#FA6900">
                    <Aleatorio min={1} max={60} />
                    <Aleatorio min={1} max={60} />
            </Card>
            <Card 
                titulo="#03 - Fragmento" 
                color="#E94C6F">
                    <Fragmento />
            </Card>
            <Card 
                titulo="#02 - Com Parametro" 
                color="#E8B71A">
                    <ComParametro
                        titulo="Situação do Aluno"
                        nome="Angelina Pierre"
                        nota={9.3}
                    />
                    <ComParametro
                        titulo="Situação do aluno"
                        nome="Santa Kaya"
                        nota="0.4"
                    />
