// IMPORT REACT + component
import React, { Component} from 'react'

// IMPORT STYLE
import './Count.css'

// IMPORTANDO COMPONENTES
import Display from './Display'
import Buttons from './Buttons'

/**
 * AULA 38 - CONTADOR EM 3 COMPONENTES
 * 
 * 
 */

// FORMA 1 - <H3> por <Display>
class Contador extends Component {
    state = {
        num: this.props.numIni || 0,
        pass: this.props.passIni
    }
    inc = () => {
        this.setState({
            num: this.state.num + this.state.pass
        })
    }
    dec = () => {
        this.setState({
            num: this.state.num - this.state.pass
        })
    }
    setPass = (evento) => {
        this.setState({
            pass: +evento.target.value
        })
    }
    render(){
        return (
            <div className='Count'>
                <h2>Contador (X-COMP)</h2>
                <Display num={this.state.num}/>
                <div>
                    <label htmlFor="passoinicial">Passo: </label>
                    <input 
                        type="number"
                        id="passoInicial"
                        value={this.state.pass}
                        onChange={this.setPass}
                    />
                </div>
                <button onClick={this.inc}>+</button>
                <button onClick={this.dec}>-</button>
            </div>
        )
    }
}

export default Contador
