            <Card titulo="#03 - Fragmento" color="#E94C6F">
                <Fragmento />
            </Card>
            <Card titulo="#02 - Com Paramentro" color="#E8B71A">
                <ComParametro
                    titulo="Situação do Aluno"
                    nome="Angelina Pierre"
                    nota={9.3}
                />
                <ComParametro
                    titulo="Situação do Aluno"
                    nome="Santa Kaya"
                    nota={0.3}
                />
            </Card>
        </div>
    </div>

