// FORMA 3.2 - A partir de um CONSTRUCTOR
class Contador extends Component {
    constructor(props){
        super(props)
        this.state = {
            numero: props.numeroInicial
        }
    }
    render(){
        return (
            <div>
                <h2> Contador </h2>
                <p>{this.state.numero}</p>
            </div>
        )
    }
}
