    return (
        <div className='UseState'>
            <PageTitle
                title = "Hook UseState"
                subtitle = "Estado em components funcionais!"
            />
            <SectionTitle
                title="Exercício #01"
            />
            <SectionTitle
                title="Exercício #02"
            />
        </div>
    )
}

export default UseState
