import React, {useReducer} from 'react';
import PageTitle from "../../components/layout/PageTitle";
import SectionTitle from '../../components/layout/SectionTitle'

const initialState = {
    cart: [],
    products: [],
    user: null,
    number: 0,
}

function reducer(state,action){
    switch(action.type){ // toda action possui um tipo
        case 'num_add2': // tipo da action(objeto)
            return ({
                ...state,
                number: state.number + 2
            })
        case 'login':
            return ({
                ...state, 
                user: {name: action.payload} 
            })
        case 'num_mult7':
            return ({
                ...state,
                number: state.number * 7
            })
            case 'num_div25':
                return ({
                    ...state,
                    number: state.number / 25
                })
            case 'numInt':
                return ({
                    ...state,
                    number: parseInt(state.number)
                })
            case 'num_addN':
                return ({
                    ...state,
                    number: state.number + action.payload
                })
        default:
            return state // default é o retorno do estado atual. alterado para resetar(initialState)
    }
}

const UseReducer = props => {
    const [state, dispatch] = useReducer(reducer, initialState)
    return (
        <div className="UseReducer">

            <PageTitle
                title="Hook UseReducer"
                subtitle="Outra forma de ter estado em componentes funcionais!"
            />

            <SectionTitle title="Exercicio #01" />
            <div className="center">
                {state.user ? 
                    <span className="text">{state.user.name}</span> :
                    <span className="text">Usuário não encontrado.</span>
                }
                <span className="text">{state.number}</span>
                <div>
                    <button className="btn" onClick={
                        () => dispatch({
                            type: 'login',
                            payload: 'Maria',
                        })
                    }>Login</button>
                    <button className="btn" onClick={
                        () => dispatch({
                            type: 'num_add2',
                        })
                    }>+2</button>
                    <button className="btn" onClick={
                        () => dispatch({
                            type: 'num_mult7'
                        })
                    }>&lowast;7</button>
                    <button className="btn" onClick={
                        () => dispatch({
                            type: 'num_div25',
                        })
                    }>&divide;25</button>
                    <button className="btn" onClick={
                        () => dispatch({
                            type:'num_addN',
                            payload: -9,
                        })
                    }>ADD N</button>
                    <button className="btn" onClick={
                        () => dispatch({
                            type:'num_addN',
                            payload: +11,
                        })
                    }>ADD N</button>
                    <button className="btn"  onClick={
                        () => dispatch({
                            type: 'numInt'
                        })
                    }>Int</button>
                </div>
            </div>
        </div>
    )
}
export default UseReducer
